import React from "react"
import "../stylesheets/Contact.css"
import "../stylesheets/ContainerTemplates.css"
import emailjs from 'emailjs-com';


class Contact extends React.Component {

    state = {
        first_name: '',
        last_name: '',
        email: '',
        subject: '',
        message: '',
    }

    onChangeHandler = (e) => {
        if (e.target.name === 'first-name') {
            this.setState({
                first_name: e.target.value
            })
        } else if (e.target.name === 'last-name') {
            this.setState({
                last_name: e.target.value
            })
        } else if (e.target.name === "email") {
            this.setState({
                email: e.target.value
            })
        } else if (e.target.name === "subject") {
            this.setState({
                subject: e.target.value
            })
        } else if (e.target.name === "message") {
            this.setState({
                message: e.target.value
            })
        }
    }

    onSubmitHandler = (e) => {
        e.preventDefault();
        emailjs.sendForm(
            'service_d89hmxa',
            'template_1rp8ehn',
            e.target,
            'user_SUJ1D5ZC8dWO5wGJ6bm0Y')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
    }



    render() {
        return(
            <div id="page-wrapper">
                <div className="buffer"></div>
                <div className="page-header">- CONTACT -</div>
                <div className="content-wrapper-contact">
                    <div className="form-wrapper">
                        <p>Have a question or want to work together?</p>
                        <form onSubmit={this.onSubmitHandler} autoComplete="off">

                            <input
                                name='first-name'
                                value={this.state.first_name}
                                placeholder="First Name"
                                onChange={this.onChangeHandler}
                            />
                            <input
                                name='last-name'
                                value-={this.state.last_name}
                                placeholder="Last Name"
                                onChange={this.onChangeHandler}
                            />
                            <input
                                name='email'
                                value-={this.state.email}
                                placeholder="Email"
                                onChange={this.onChangeHandler}
                            />
                            <input
                                id="subject"
                                name='subject'
                                value={this.state.subject}
                                placeholder="Subject"
                                onChange={this.onChangeHandler}
                            />
                            <input
                                id="message"
                                name='message'
                                value={this.state.message}
                                placeholder="Message"
                                onChange={this.onChangeHandler}
                            />
                            <input id='submit' name='submit' type='submit' value="Submit" />
                        </form>
                    </div>
                    <div className="footer-wrapper">
                        <div className="footer-link">
                            <g xmlns="http://www.w3.org/2000/svg" id="linkedin"><g id="LinkedIn-2" data-name="LinkedIn"><path d="M95,95H75.71a2,2,0,0,1-2-2V62c0-11.13-5.85-11.13-7.77-11.13-5.15,0-7.34,4.06-8,5.81a11.3,11.3,0,0,0-.5,4V93a2,2,0,0,1-2,2H36.1a2,2,0,0,1-1.41-.59A2,2,0,0,1,34.1,93c0-.53.26-52.59,0-57.92a2,2,0,0,1,.55-1.48A2,2,0,0,1,36.1,33H55.4a2,2,0,0,1,2,2v2.54c3.21-3.17,8-5.9,15.39-5.9C88,31.61,97,42.12,97,59.73V93A2,2,0,0,1,95,95ZM77.71,91H93V59.73c0-15.33-7.37-24.12-20.21-24.12-8.93,0-13.09,4.64-15.71,8.68A2,2,0,0,1,53.4,43.2V37H38.15c.16,9.85,0,45.08,0,54H53.4V60.59a14.52,14.52,0,0,1,.79-5.46c2.09-5.19,6.48-8.3,11.75-8.3,4.39,0,11.77,2,11.77,15.13ZM25.44,95H6.15a2,2,0,0,1-2-2V35a2,2,0,0,1,2-2H25.44a2,2,0,0,1,2,2V93A2,2,0,0,1,25.44,95ZM8.15,91H23.44V37H8.15Zm7.64-61.94h-.13C8.33,29.06,3,24,3,17S8.43,5,15.92,5,28.55,9.93,28.71,17C28.71,24,23.27,29.06,15.79,29.06ZM15.92,9C10.58,9,7,12.22,7,17s3.56,8,8.66,8h.13c5.33,0,8.92-3.23,8.92-8S21.15,9,15.92,9Z"/></g></g>
                        </div>
                    </div>

                </div>




            </div>
        )
    }
}

export default Contact
