import React from "react"
import "../stylesheets/Contact.css"
import "../stylesheets/ContainerTemplates.css"
import emailjs from 'emailjs-com';


class Contact extends React.Component {

    state = {
        first_name: '',
        last_name: '',
        email: '',
        subject: '',
        message: '',
    }

    onChangeHandler = (e) => {
        if (e.target.name === 'first-name') {
            this.setState({
                first_name: e.target.value
            })
        } else if (e.target.name === 'last-name') {
            this.setState({
                last_name: e.target.value
            })
        } else if (e.target.name === "email") {
            this.setState({
                email: e.target.value
            })
        } else if (e.target.name === "subject") {
            this.setState({
                subject: e.target.value
            })
        } else if (e.target.name === "message") {
            this.setState({
                message: e.target.value
            })
        }
    }

    onSubmitHandler = (e) => {
        e.preventDefault();
        emailjs.sendForm(
            'service_d89hmxa',
            'template_1rp8ehn',
            e.target,
            'user_SUJ1D5ZC8dWO5wGJ6bm0Y')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
    }



    render() {
        return(
            <div id="page-wrapper">
                <div className="page-header">- CONTACT -</div>

                <div className="content-wrapper-contact">
                    <div className="form-wrapper">
                        <p>Have a question or want to work together?</p>
                        <form onSubmit={this.onSubmitHandler} autoComplete={false}>

                            <input
                                name='first-name'
                                value={this.state.first_name}
                                placeholder="First Name"
                                onChange={this.onChangeHandler}
                            />
                            <input
                                name='last-name'
                                value-={this.state.last_name}
                                placeholder="Last Name"
                                onChange={this.onChangeHandler}
                            />
                            <input
                                name='email'
                                value-={this.state.email}
                                placeholder="Email"
                                onChange={this.onChangeHandler}
                            />
                            <input
                                id="subject"
                                name='subject'
                                value={this.state.subject}
                                placeholder="Subject"
                                onChange={this.onChangeHandler}
                            />
                            <input
                                id="message"
                                name='message'
                                value={this.state.message}
                                placeholder="Message"
                                onChange={this.onChangeHandler}
                            />
                            <input id='submit' name='submit' type='submit' value="Submit" />
                        </form>

                    </div>





                </div>




            </div>
        )
    }
}

export default Contact
