import React from 'react'
import "../stylesheets/Home.css"

const homeDivWrapper = document.getElementById("home")

class Home extends React.Component {

    genRandomNumberRange = (min, max) => {
        return Math.floor(Math.random() * (min + max) + min)
    }

    rotate = (index) => {
        const boxes = document.querySelectorAll("div.circle")
        boxes.forEach(box => {
            box.style.transform = "rotate" +
                `(${String(Number(box.dataset.rotationStart) +
                    Number(box.dataset.rotationSpeed) * index /8)}deg)`
        })
    }

    genCircles = () => {
        let index = 0
        while (index < 25) {
            const circle = document.createElement('div')
            circle.className = "circle"
            const size = this.genRandomNumberRange(100, 2000)
            const sizeProperty = String( + size) + "px"

            circle.dataset.rotationStart = String(Math.floor(Math.random() * Math.floor(360)))
            let min = Math.ceil(2);
            let max = Math.floor(3);

            circle.dataset.rotationSpeed = String(Math.floor(Math.random() * (min + max) + min))

            circle.style.transform = "rotate" + `(${circle.dataset.rotationStart}deg)`
            circle.style.width = sizeProperty
            circle.style.height = sizeProperty
            circle.style.borderRadius = "50%"

            circle.style.left = "calc" + `(50% - ${size/2}px)`
            circle.style.top = "calc" + `(50% - ${size/2}px)`
            //circle.style.zIndex = `-${String(index)}`

            let starIndex = 0
            while (starIndex < 200) {
                const colors = [
                    '#1175ff',
                    '#bbff00',
                    '#37cbff',
                    '#1b91f5',
                    '#6ea1e2',
                    '#602ed7']
                const star = document.createElement("div")
                star.className = "star5px"
                star.style.left = String(this.genRandomNumberRange(1, 100)) + "%"
                star.style.top = String(this.genRandomNumberRange(1, 100)) + "%"
                star.style.backgroundColor = `${String(colors[this.genRandomNumberRange(0, 6)])}`
                circle.append(star)
                starIndex++
            }
            homeDivWrapper.append(circle)
            index++
        }
        this.runRotate()
    }

    async runRotate() {
        let index = 0
        while (index < Infinity) {
            await new Promise(r => setTimeout(r, 150));
            this.rotate(index)
            index++
        }
    }

    render(){
        return(
            <div>{this.genCircles()}</div>
        )
    }
}

export default Home
