import React from 'react'
import "../stylesheets/Home.css"
import Circles from "../Cards/Circles";

class Home extends React.Component {

    state = {
        circles: ""
    }

    componentDidMount() {
        this.setState({
            circles: this.genCircles()
        })
    }

    genRandomNumberRange = (min, max) => {
        return Math.floor(Math.random() * (min + max) + min)
    }

    rotate = (index) => {
        const boxes = document.querySelectorAll("div.circle")
        boxes.forEach(box => {
            box.style.transform = "rotate" +
                `(${String(Number(box.dataset.rotationStart) +
                    Number(box.dataset.rotationSpeed) * index /8)}deg)`
        })
    }

    genCircles = () => {
        let circleList = []
        let index = 1
        while (index < 25) {
            let min = Math.ceil(2);
            let max = Math.floor(3);
            const size = this.genRandomNumberRange(100, 2000)

            let circle = {
                id: index,
                className: "circle",
                size: String(size) + "px",
                rotation_start: Math.floor(Math.random() * Math.floor(360)),
                rotation_speed: String(Math.floor(Math.random() * (min + max) + min)),
                left: size / 2,
                top: size / 2,
                stars: []
            }

            let starIndex = 0
            let stars = []
            while (starIndex < 200) {
                const colors = [
                    '#1175ff',
                    '#bbff00',
                    '#37cbff',
                    '#1b91f5',
                    '#6ea1e2',
                    '#602ed7']

                let star = {
                    id: starIndex,
                    class_name: "star5px",
                    left: String(this.genRandomNumberRange(1, 100)) + "%",
                    top: String(this.genRandomNumberRange(1, 100)) + "%",
                    background_color: `${String(colors[this.genRandomNumberRange(0, 6)])}`
                }
                stars.push(star)
                starIndex++
            }
            circle.stars = stars

            circleList.push(circle)
            index++
        }
        return circleList
    }

    renderCircles = () => {
        const circles = this.genCircles()
        return circles.map(circle => <Circles key={circle.id} circle={circle}/>)
    }

    runRotate = () => {
        let index = 0
        while (index < Infinity) {
            // new Promise(r => setTimeout(r, 150));
            this.rotate(index)
            index++
        }
    }

    render(){
        // console.log(this.genCircles())
        return(
            <div id='home-wrapper'>
                <div id="home">{this.renderCircles()}</div>
            </div>

        )
    }
}

export default Home
